package rsautil

import (
	"encoding/base64"
	"strconv"
	"strings"
)

var serverRandomness = "H2ulzLlh7E0="
var priKey = ""

func CreateSign(cRandomness string, guid string, offline bool, validFrom string, validTo string) string {
	ret := ""
	if offline {
		ret = strings.Join([]string{cRandomness, serverRandomness, guid, strconv.FormatBool(offline), validFrom, validTo}, ";")
	} else {
		ret = strings.Join([]string{cRandomness, serverRandomness, guid, strconv.FormatBool(offline)}, ";")
	}
	pk, _ := PrivateKeyFrom64(priKey)
	sign, _ := PrivateSign(pk, []byte(ret))
	return base64.StdEncoding.EncodeToString(sign)
}
